// **** 圖片路徑處理。( 用來處理將 "/"、"www"、"cache" 開頭路徑，改連到 img Server )
var selfDomainUrl = "http://" + window.location.host + "/";
var webDomainUrl = "http://www.wantgoo.com/"; // 正式機
var cacheDomainUrl = "http://cache.wantgoo.com/"; // Cache Server
var imgDomainUrl = "http://img.wantgoo.com/WantGooFiles/"; // Image Server
var memberPostFilesBaseDir = "updatefiles";
var memberPostFilesThumbBaseDir = "updatefilesthumb";
var memberHeadImageBaseDir = "uploadfiles";
var memberUserFilesBaseDir = "userfile";
var memberVedioBaseDir = "Video/";
// 圖片路徑處理。( 用來處理將 "/"、"www"、"cache" 開頭路徑，改連到 img Server )
function imgSrcProcess() {
    // 是同網域的圖片 (外連圖片則直接跳過此區塊判斷)
    if (location.href.indexOf(selfDomainUrl) >= 0) {
        $("img, input:image").each(function () {
            if (this.src != null && this.src != "") {
                var thisSrc_ToLower = this.src.toLowerCase();
                var indexDirKeyWord = 0;
                // 會員上傳圖檔 (指向 Image Server)
                indexDirKeyWord = thisSrc_ToLower.indexOf(memberPostFilesBaseDir.toLowerCase())
                if (indexDirKeyWord >= 0) {
                    var nowDomain = thisSrc_ToLower.substring(0, indexDirKeyWord); // 現在的
                    var newDomain = thisSrc_ToLower.replace(nowDomain, imgDomainUrl) // 新的
                    this.src = newDomain;
                }
                // 會員上傳圖檔(縮圖) (指向 Image Server)
                indexDirKeyWord = thisSrc_ToLower.indexOf(memberPostFilesThumbBaseDir.toLowerCase())
                if (indexDirKeyWord >= 0) {
                    this.src = this.src.toLowerCase().replace(memberPostFilesThumbBaseDir, memberPostFilesBaseDir);
                }
                // 會員頭像 (指向 Image Server)
                indexDirKeyWord = thisSrc_ToLower.indexOf(memberHeadImageBaseDir.toLowerCase())
                if (indexDirKeyWord >= 0) {
                    var nowDomain = thisSrc_ToLower.substring(0, indexDirKeyWord); // 現在的
                    var newDomain = thisSrc_ToLower.replace(nowDomain, imgDomainUrl) // 新的
                    this.src = newDomain;
                }
                // UserFile (指向 Image Server)
                indexDirKeyWord = thisSrc_ToLower.indexOf(memberUserFilesBaseDir.toLowerCase())
                if (indexDirKeyWord >= 0) {
                    var nowDomain = thisSrc_ToLower.substring(0, indexDirKeyWord); // 現在的
                    var newDomain = thisSrc_ToLower.replace(nowDomain, imgDomainUrl) // 新的
                    this.src = newDomain;
                }
            }
        });
        $("a").each(function () {
            if (this.href != null && this.href != "") {
                var thisSrc_ToLower = this.href.toLowerCase();
                // 會員上傳圖檔 (指向 Image Server)
                indexDirKeyWord = thisSrc_ToLower.indexOf(memberPostFilesBaseDir.toLowerCase())
                if (indexDirKeyWord >= 0) {
                    var nowDomain = thisSrc_ToLower.substring(0, indexDirKeyWord); // 現在的
                    var newDomain = thisSrc_ToLower.replace(nowDomain, imgDomainUrl) // 新的
                    this.href = newDomain;
                }
            }
        });
    };
};
$(function () { imgSrcProcess(); });